// apiRequest.js
import axios from "axios";

const API_URL = "http://localhost:8080";

// Function to set JWT token in Axios headers and sessionStorage
const setAuthToken = (token) => {
  if (token) {
    axios.defaults.headers.common["Authorization"] = `Bearer ${token}`;
    sessionStorage.setItem("jwtToken", token);
  } else {
    delete axios.defaults.headers.common["Authorization"];
    sessionStorage.removeItem("jwtToken");
  }
};

// Function to get JWT token from sessionStorage
const getAuthToken = () => {
  return sessionStorage.getItem("jwtToken");
};

// Register a new user
const registerUser = async (formData) => {
  try {
    const response = await axios.post(`${API_URL}/register-user`, formData);
    return response.data;
  } catch (error) {
    throw new Error("Registration failed");
  }
};

// Log in a user
const loginUser = async (credentials) => {
  try {
    const response = await axios.post(`${API_URL}/login`, credentials);
    const { token } = response.data;
    setAuthToken(token); // Set JWT token
    return response.data;
  } catch (error) {
    throw new Error("Login failed");
  }
};

// Get user data by user ID
const getUser = async (userId) => {
  try {
    const token = getAuthToken();
    const response = await axios.get(`${API_URL}/user/${userId}`, {
      headers: {
        Authorization: `Bearer ${token}`
      }
    });
    return response.data;
  } catch (error) {
    throw new Error("Failed to get user data");
  }
};

// Update user data by user ID
const updateUser = async (userId, userData) => {
  try {
    const token = getAuthToken();
    const response = await axios.put(`${API_URL}/user/${userId}`, userData, {
      headers: {
        Authorization: `Bearer ${token}`
      }
    });
    return response.data;
  } catch (error) {
    throw new Error("Failed to update user");
  }
};

// Delete a user by user ID
const deleteUser = async (userId) => {
  try {
    const token = getAuthToken();
    const response = await axios.delete(`${API_URL}/user/${userId}`, {
      headers: {
        Authorization: `Bearer ${token}`
      }
    });
    return response.data;
  } catch (error) {
    throw new Error("Failed to delete user");
  }
};

// Get all users
const getAllUsers = async () => {
  try {
    const token = getAuthToken();
    const response = await axios.get(`${API_URL}/users`, {
      headers: {
        Authorization: `Bearer ${token}`
      }
    });
    return response.data;
  } catch (error) {
    throw new Error("Failed to get all users");
  }
};

export { registerUser, loginUser, getUser, updateUser, deleteUser, getAllUsers, setAuthToken };
