import React, { useState } from 'react';
import { registerUser } from '../api/apiRequest';

function Register() {
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [registrationSuccess, setRegistrationSuccess] = useState(false); // State to manage registration success banner

  const handleSubmit = async (e) => {
    e.preventDefault();

    const regUserData = {
      firstName: firstName,
      lastName: lastName,
      email: email,
      password: password
    };

    // Call the registerUser function from the API to register the user
    try {
      await registerUser(regUserData);
      setRegistrationSuccess(true); // Set registration success to true
      // Reset form fields after successful registration
      setFirstName('');
      setLastName('');
      setEmail('');
      setPassword('');
      // Hide registration success banner after 5 seconds
      setTimeout(() => {
        setRegistrationSuccess(false);
      }, 5000);
    } catch (error) {
      console.error('Registration failed:', error);
    }
  };

  return (
    <div>
      <h2>Registration Form</h2>
      {/* Render the success message only when registrationSuccess is true */}
      {registrationSuccess && <div>Successful registration!</div>}
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="firstName">First Name:</label>
          <input
            type="text"
            id="firstName"
            value={firstName}
            onChange={(e) => setFirstName(e.target.value)}
          />
        </div>
        <div>
          <label htmlFor="lastName">Last Name:</label>
          <input
            type="text"
            id="lastName"
            value={lastName}
            onChange={(e) => setLastName(e.target.value)}
          />
        </div>
        <div>
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div>
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <button type="submit">Register</button>
      </form>
    </div>
  );
}

export default Register;
