import React, { useState, useEffect } from 'react';
import { getUser, deleteUser, getAllUsers, updateUser } from '../api/apiRequest'; // Assuming you have these functions defined in your API

function Home() {
  const [userData, setUserData] = useState(null);
  const [allUsers, setAllUsers] = useState([]);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const data = await getUser();
        setUserData(data);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUserData();
  }, []); // Empty dependency array ensures this effect runs only once when the component mounts

  const handleGetAllUsers = async () => {
    try {
      const users = await getAllUsers();
      setAllUsers(users);
    } catch (error) {
      console.error('Error fetching all users:', error);
    }
  };

  const handleDeleteUser = async (userId) => {
    try {
      await deleteUser(userId);
      // Refresh user data after deletion
      const data = await getUser();
      setUserData(data);
    } catch (error) {
      console.error('Error deleting user:', error);
    }
  };

  const handleEditUser = async (userId) => {
    try {
      await updateUser(userId);
      // Refresh user data after editing
      const data = await getUser();
      setUserData(data);
    } catch (error) {
      console.error('Error editing user:', error);
    }
  };

  return (
    <div className="container mt-5">
      <header>
        <h1 className="text-center">Welcome {userData ? userData.email : ''}</h1>
      </header>
      <main>
        <section>
          <h2 className="text-center mb-4">User Management</h2>
          <div className="d-flex justify-content-center">
            <button className="btn btn-primary mb-3" onClick={handleGetAllUsers}>Get All Users</button>
          </div>
          <table className="table">
            <thead>
              <tr>
                <th>S.no</th>
                <th>First Name</th>
                <th>Last Name</th>
                <th>Email</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {allUsers.map((user, index) => (
                <tr key={user.id}>
                  <td>{index + 1}</td>
                  <td>{user.firstName}</td>
                  <td>{user.lastName}</td>
                  <td>{user.email}</td>
                  <td>
                    <button className="btn btn-info me-2" onClick={() => handleEditUser(user.id)}>Edit</button>
                    <button className="btn btn-danger" onClick={() => handleDeleteUser(user.id)}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </section>
      </main>
    </div>
  );
}

export default Home;
