import React, { useState, useEffect } from 'react';
import { getUser, deleteUser, getAllUsers, updateUser } from '../api/apiRequest'; // Assuming you have these functions defined in your API

function Home() {
  const [userData, setUserData] = useState(null);
  const [allUsers, setAllUsers] = useState([]);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const data = await getUser();
        setUserData(data);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUserData();
  }, []); // Empty dependency array ensures this effect runs only once when the component mounts

  const handleGetAllUsers = async () => {
    try {
      const users = await getAllUsers();
      setAllUsers(users);
    } catch (error) {
      console.error('Error fetching all users:', error);
    }
  };

  const handleDeleteUser = async (userId) => {
    try {
      await deleteUser(userId);
      // Refresh user data after deletion
      const data = await getUser();
      setUserData(data);
    } catch (error) {
      console.error('Error deleting user:', error);
    }
  };

  const handleEditUser = async (userId) => {
    try {
      await editUser(userId);
      // Refresh user data after editing
      const data = await getUser();
      setUserData(data);
    } catch (error) {
      console.error('Error editing user:', error);
    }
  };

  return (
    <div>
      <header>
        <h1>Welcome {userData ? userData.email : ''}</h1>
      </header>
      <main>
        <section>
          <h2>About Us</h2>
          <p>We are a team dedicated to creating awesome React.js applications.</p>
        </section>
        <section>
          <h2>Services</h2>
          <p>Our services include web development, mobile app development, and consulting.</p>
        </section>
        <section>
          <h2>Contact Us</h2>
          <p>Email: contact@example.com</p>
          <p>Phone: 123-456-7890</p>
        </section>
        <section>
          <h2>User Management</h2>
          <button onClick={handleGetAllUsers}>Get All Users</button>
          <ul>
            {allUsers.map(user => (
              <li key={user.id}>
                {user.name} - {user.email}
                <button onClick={() => handleEditUser(user.id)}>Edit</button>
                <button onClick={() => handleDeleteUser(user.id)}>Delete</button>
              </li>
            ))}
          </ul>
        </section>
      </main>
    </div>
  );
}

export default Home;
